    /** Set URL info to fields */
    // jdbc:mysql://localhost:3306/mida
    public void setURL(String sURL) {
        int n, nPos;
        if (sURL.length() == 0) return;
        // Driver
        nPos = sURL.indexOf(":",5);
        jDriverField.setText(sURL.substring(0, nPos));
        n = nPos;
        // Host
        nPos = sURL.indexOf(":",n+1);
        jHostField.setText(sURL.substring(n+3,nPos));
        n = nPos;
        // Port
        nPos = sURL.indexOf("/",n+1);
        jPortField.setText(sURL.substring(n+1,nPos));
        n = nPos;
        // DB name
        jDatabaseField.setText(sURL.substring(n+1));
    }

    /** Set URL info to fields */
    public String getURL() {
        String s = new String();
        s = jDriverField.getText();
        s += "://";
        s += jHostField.getText();
        s += ":";
        s += jPortField.getText();
        s += "/";
        s += jDatabaseField.getText();
        return s;
    }
    
